Layer.OnCommand:
%function(self)
			self:zoom(gGraphicsScale);
			self:x(SCREEN_CENTER_X);
			self:y(SCREEN_CENTER_Y);
			self:z(0.00);
			gHeavenMuted = self;
		end

%function(self)
			self:zoom(gGraphicsScale);
			self:x(SCREEN_CENTER_X);
			self:y(SCREEN_CENTER_Y);
			self:z(0.01);
			gHeaven = self;
			self:diffuse(255,255,255,0);
			self:decelerate(20);
			self:diffusealpha(1);
		end

%function(self)
			self:zoom(gGraphicsScale);
			self:x(SCREEN_CENTER_X);
			self:y(SCREEN_HEIGHT + self:GetHeight() / 2);
			self:z(0.021);
			self:clearzbuffer(1);
			self:blend('noeffect');
			self:zwrite(1);
			table.insert(gSun, self);
		end

%function(self)
			self:zoom(gGraphicsScale);
			self:x(SCREEN_CENTER_X);
			self:y(SCREEN_HEIGHT / 2);
			self:z(0.022);
			self:ztestmode('writeonfail');
			table.insert(gSunGradient, self);
			self:diffuse(255,255,255,0.1);
		end

%function(self)
			self:zoom(gGraphicsScale);
			self:x(SCREEN_CENTER_X);
			self:y(SCREEN_HEIGHT + self:GetHeight() / 2);
			self:z(0.023);
			self:clearzbuffer(1);
			self:blend('noeffect');
			self:zwrite(1);
			table.insert(gSun, self);
		end

%function(self)
			self:zoom(gGraphicsScale);
