%function(self)
			self:zoom(gGraphicsScale);
			self:x(SCREEN_WIDTH * 0.25);
			self:y(SCREEN_HEIGHT / 2);
			self:z(0.03);
			self:blend('add');
			gTrunk[1][3] = self;
		end

%function(self)
			self:zoom(gGraphicsScale);
			self:x(SCREEN_WIDTH * 0.75);
			self:y(SCREEN_HEIGHT / 2);
			self:z(0.03);
